package cal;

public class CalTest3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//비교연산자
		int x =200;
		int y =100;
		System.out.println("x가 y보다큰가?" + (x>y));
		System.out.println("x가 y보다동일한가? " + (x==y));
		System.out.println("x가 y보다다른가? " + (x!=y));
		
		
		
	}

}
